import './App.css';
import { BrowserRouter as Router, Route, Routes } from 'react-router-dom';
import SideNav from '../SideNav/SideNav';
import MainContent from '../MainContent/MainContent';
import InvoiceDetails from '../InvoiceDetails/InvoiceDetails';
import { InvoiceFooter } from '../InvoiceDetails/InvoiceDetails';
import EditFormContainer from '../FormEditing/FormEditing';

import { useState } from 'react';

function App() {
  const [ displayFooter, setDisplayFooter ] = useState(false);
  const [ formData, setFormData ] = useState(null);

  const handleDisplayFooter = () => {
    setDisplayFooter(!displayFooter);
  }

  return (
    <div className='App'>
      <Router>
        <Routes>
          <Route exact path='/' element={<MainContent handleDisplayFooter={handleDisplayFooter}/>} />
          <Route path='/invoice/:id' element={<InvoiceDetails formData={formData} setFormData={setFormData} handleDisplayFooter={handleDisplayFooter}/>} />
          <Route path='/invoice/edit/:id/' element={<EditFormContainer form={formData}/>} />
        </Routes>
        {displayFooter && formData && <InvoiceFooter id={formData.id}/>}
      </Router>

    </div>
  );
}



export default App;
