import { useState, useEffect } from 'react';
import backArrow from '../../images/assets/icon-arrow-left.svg';
import arrowPlus from '../../images/assets/icon-plus.svg';
import {BillFrom, BillTo, NewItem} from './FormEditing';
import { NewInvoiceFooter } from '../InvoiceDetails/InvoiceDetails';

const NewFormContainer = () => {
    const [form, setForm] = useState({
        'id': '',
        'createdAt': '',
        'paymentDue': '',
        'description': '',
        'paymentTerms': null,
        'clientName': '',
        'clientEmail': '',
        'status': '',
        'senderAddress': {
            'street': '',
            'city': '',
            'postCode': '',
            'country': '',
        },
        'clientAddress': {
            'street': '',
            'city': '',
            'postCode': '',
            'country': ''
        },
        'items': [],
    });
    const [editedForm, setEditedForm] = useState(form);
    const [submitPending, setSubmitPending] = useState(false);

    const handleInputChange = (field, value) => {
    
        // Split the field into its components (if it's a nested field)
        const fieldComponents = field.split('-');

        if (fieldComponents.length === 1) {
            // If it's not a nested field, update directly
            setEditedForm(prevForm => ({
                ...prevForm,
                [field]: value
            }));
        } else if (fieldComponents.length === 2) {
            // If it's a nested field, update accordingly
            const [parentField, nestedField] = fieldComponents;
            setEditedForm(prevForm => ({
                ...prevForm,
                [parentField]: {
                    ...prevForm[parentField],
                    [nestedField]: value
                }
            }));
        }
    };

    const handleSavePend = () => {
        setSubmitPending(true);
    }

    return (
        <>
            <div className='New-Form-Wrapper'>
                <NewForm form={form} handleInputChange={handleInputChange} setEditedForm={setEditedForm} editedForm={editedForm}/>
            </div>
            <NewInvoiceFooter handleSavePend={handleSavePend} />
        </>
    );
};

const NewForm = ({form, handleInputChange, editedForm}) => {

    return (
        <div className='New-Form-Wrapper'>
            <div className={`return-page-wrapper`}>
                <img src={backArrow} alt=''/>
                Go back
            </div>
            <div className='New-Form'>
                <h2>New Invoice</h2>
                <form>
                    <BillFrom editedForm={editedForm} handleInputChange={handleInputChange}/>
                    <BillTo editedForm={editedForm} handleInputChange={handleInputChange}/>
                    <NewBill items={form.items} />
                </form>
            </div>
        </div>
    )
};


const NewBill = ({ items }) => {
    const [newItems, setNewItems] = useState([]);
    const [formInputs, setFormInputs] = useState({
        name: '',
        qty: '',
        price: '',
        total: ''
    });

    const handleFormInputChange = (field, value) => {
        setFormInputs(prevInputs => ({
            ...prevInputs,
            [field]: value
        }));
    };

    const handleFormSubmit = (event) => {
        event.preventDefault();
        const newItem = { ...formInputs };
        setNewItems(prevItems => [...prevItems, newItem]);
        setFormInputs({
            name: '',
            qty: '',
            price: '',
            total: ''
        });
    };

    return (
        <div className='ItemList-Wrapper'>
            <h3 id='item-list'>Item List</h3>
            <div className='Item-Form'>
                <form onSubmit={handleFormSubmit}>
                    <input
                        type='text'
                        placeholder='Name'
                        value={formInputs.name}
                        onChange={e => handleFormInputChange('name', e.target.value)}
                    />
                    <input
                        type='number'
                        placeholder='Qty'
                        value={formInputs.qty}
                        onChange={e => handleFormInputChange('qty', e.target.value)}
                    />
                    <input
                        type='number'
                        placeholder='Price'
                        value={formInputs.price}
                        onChange={e => handleFormInputChange('price', e.target.value)}
                    />
                    <button type='submit'>Add Item</button>
                </form>
            </div>
            <div className='Items-ItemList'>
                {newItems.map((item, index) => (
                    <div className='Item-Wrapper' key={index}>
                        <div className='Input-Wrapper'>
                            <label htmlFor='item-name' id='item-name-label'>Item Name</label>
                            <input
                                type='text'
                                name='item-name'
                                value={item.name}
                                readOnly
                            />
                        </div>
                        <div className='Qty-Container item-container'>
                            {/* Display qty */}
                        </div>
                        <div className='Price-Container item-container'>
                            {/* Display price */}
                        </div>
                        <div className='Total-Container item-container'>
                            {/* Display total */}
                        </div>
                    </div>
                ))}
            </div>
        </div>
    );
};



export default NewFormContainer;
