import './App.css';
import { BrowserRouter as Router, Route, Routes } from 'react-router-dom';
import SideNav from '../SideNav/SideNav';
import MainContent from '../MainContent/MainContent';
import InvoiceDetails from '../InvoiceDetails/InvoiceDetails';
import { InvoiceFooter } from '../InvoiceDetails/InvoiceDetails';
import EditFormContainer from '../FormEditing/FormEditing';
import NewFormContainer from '../FormEditing/FormCreation.js';

import { useState, useEffect } from 'react';
import { GetAllInvoices } from '../InvoiceDetails/NewInvoices';

function App() {
  const [ formData, setFormData ] = useState(null); // all of the forms stored locally
  const [ selectedInvoice, setSelectedInvoice ] = useState(null); // the invoice user selects
  const [newItems, setNewItems] = useState([]); // items section of JSON object
  const [editedForm, setEditedForm] = useState({
      'id': '',
      'createdAt': '',
      'paymentDue': '',
      'description': '',
      'paymentTerms': null,
      'clientName': '',
      'clientEmail': '',
      'status': '',
      'senderAddress': {
          'street': '',
          'city': '',
          'postCode': '',
          'country': '',
      },
      'clientAddress': {
          'street': '',
          'city': '',
          'postCode': '',
          'country': ''
      },
      'items': [],
  }); // form to edit/new 

  useEffect(() => {
    const invoices = GetAllInvoices();
    setFormData(invoices);
  }, []);

  const handleInputChange = (field, value) => {
    // Split the field into its components (if it's a nested field)
    const fieldComponents = field.split('-');

    if (fieldComponents.length === 1) {
        // If it's not a nested field, update directly
        setEditedForm(prevForm => ({
            ...prevForm,
            [field]: value
        }));
        console.log(editedForm[field], value);
    } else if (fieldComponents.length === 2) {
        // If it's a nested field, update accordingly
        const [parentField, nestedField] = fieldComponents;
        setEditedForm(prevForm => ({
            ...prevForm,
            [parentField]: {
                ...prevForm[parentField],
                [nestedField]: value
            }
        }));
    }
};


const handleNewItem = () => {
  setNewItems(prevItems => [...prevItems, {"name": '', "quantity": null, "price": null, "total": null}]); // Add an empty object
}

const handleFormInputChange = (index, field, value) => {
  setNewItems(prevItems => {
      const updatedItems = [...prevItems];
      updatedItems[index] = {
          ...updatedItems[index],
          [field]: value
      };
      updatedItems[index].total = updatedItems[index].quantity * updatedItems[index].price;
      return updatedItems;
  });
};

  return (
    <div className='App'>
      <Router>
        <SideNav />
        <Routes>
          <Route exact path='/' element={<MainContent data={formData} />} />
          <Route path='/invoice/:id' element={<InvoiceDetails formData={formData} selectedInvoice={selectedInvoice} setSelectedInvoice={setSelectedInvoice} />} />
          <Route path='/invoice/edit/:id/' element={<EditFormContainer form={selectedInvoice} handleInputChange={handleInputChange} setEditedForm={setEditedForm} />} />
          <Route path='/invoice/new' element={<NewFormContainer handleInputChange={handleInputChange} handleNewItem={handleNewItem} handleFormInputChange={handleFormInputChange} newItems={newItems} editedForm={editedForm} />} />
        </Routes>
      </Router>

    </div>
  );
}



export default App;
