import { useEffect } from 'react';
import backArrow from '../../images/assets/icon-delete.svg';
import BillForm from './FormEditing';

const NewFormContainer = () => {
    const [form, setForm] = useState(null);

    useEffect(() => {
        setForm({
            'id': '',
            'createdAt': '',
            'paymentDue': '',
            'description': '',
            'paymentTerms': null,
            'clientName': '',
            'clientEmail': '',
            'status': '',
            'senderAddress': {
                'street': '',
                'city': '',
                'postCode': '',
                'country': '',
            },
            'clientAddress': {
                'street': '',
                'city': '',
                'postCode': '',
                'country': ''
            },
            'items': [],
        });
    }, []);

    return (
        <div className='New-Form-Wrapper'>
            <NewForm form={form} />
            <NewFormFooter form={form} />
        </div>
    );
};

const NewForm = ({form}) => {
    return (
        <div className='New-Form-Wrapper'>
            <div
                className={`return-page-wrapper`}
            >
                <img src={backArrow} alt=''/>
                Go back
            </div>
            <div className='New-Form'>
                <h2>New Invoice</h2>
                <BillForm form={form} />
                <BillTo form={form} />
            </div>
        </div>
    )
};


export default NewFormContainer;
