import { useState } from 'react';
import backArrow from '../../images/assets/icon-arrow-left.svg';
import { NewInvoiceFooter } from '../InvoiceDetails/InvoiceDetails';
import { FormData } from './FormData'

const NewFormContainer = () => {
    const [sendButton, setSendButton] = useState(false);
    const [form, setForm] = useState({
        'id': '',
        'createdAt': '',
        'paymentDue': '',
        'description': '',
        'paymentTerms': 1,
        'clientName': '',
        'clientEmail': '',
        'status': '',
        'senderAddress': {
            'street': '',
            'city': '',
            'postCode': '',
            'country': '',
        },
        'clientAddress': {
            'street': '',
            'city': '',
            'postCode': '',
            'country': ''
        },
        'items': [],
    });

    const handleSubmission = () => {
        setSendButton(true);
    }

    return (
        <>
            <div className='New-Form-Wrapper'>
                <div className='New-Form-Wrapper'>
                    <div className={`return-page-wrapper`}>
                        <img src={backArrow} alt=''/>
                        Go back
                    </div>
                    <div className='New-Form'>
                        <h2>New Invoice</h2>
                        <FormData form={form} handleSave={sendButton} />
                    </div>
                </div>
            </div>
            <NewInvoiceFooter handleSubmit={handleSubmission}/>
        </>
    );
};




export default NewFormContainer;
