import './MainContent.css';

import dropDownArrow from '../../images/assets/icon-arrow-down.svg';
import iconPlus from '../../images/assets/icon-plus.svg';

const MainContent = () => {
    return (
        <main className='main-content-wrapper'>
            <Header />
            <InvoicesList />
        </main>
    )
}


const Header = () => {
    const amountInvoices = 7;
    return (
        <>
            <div className='Header-content-wrapper'>
                <div className='Header-text-wrapper'>
                    <h1>Invoices</h1>
                    <p className='header-subheading total-invoices'>{amountInvoices} invoices.</p>
                </div>
                <div className='Dropdown_Wrapper'>
                    <button id='dropdown-filter-header' aria-controls='dropdown-filter-options'
                    className='dropdown-header'>
                        <span className='Filter-text'>
                            Filter 
                            <span> by status</span>    
                        </span>
                        <img src={dropDownArrow} alt=''/>
                    </button>
                </div>
                <div className='Buttons-New-Wrapper'>
                    <span className='Buttons-Icon-Wrapper'>
                        <img src={iconPlus} alt=''/>
                    </span>
                    New 
                    <span className='btn-invoice-addition'>
                         Invoice
                    </span>
                </div>
            </div>
        </>
    )
}

const InvoicesList = () => {
    return (
        <>
            <div className='invoices-list-wrapper'></div>
        </>
    )
}

export default MainContent;
