import './FormEditing.css'
import backArrow from '../../images/assets/icon-arrow-down.svg';


const EditFormContainer = ({form}) => {
    return (
      <div className='Editting-Form-Wrapper'>
        <EditForm form={form}/>
        <EditFormFooter form={form} />
      </div>
    );
};


const EditForm = ({form}) => {
    return (
        <div className='Editting-Form-Wrapper'>
            <div
                className={`return-page-wrapper`}
            >
                <img src={backArrow} alt=''/>
                Go back
            </div>
            <div className='Editing-Form'>
                <h2>Edit {form.id}</h2>
                <BillForm form={form} />
            </div>
        </div>
    );
};

const BillForm = ({form}) => {
    return (
        <div className='Bill-From-Wrapper'>
            <h4>Bill From</h4>
            <div className='Street-Address-Container form-container'>
                <label for='street'>Street Address</label>
                <input type='text' name='street' placeholder={form.senderAddress.street}/>
            </div>
            <div className='City-Postcode-Wrapper'>
                <div className='City-Container form-container'>
                    <label for='city'>City</label>
                    <input type='text' name='city' placeholder={form.senderAddress.city} />
                </div>
                <div className='Postcode-Container form-container'>
                    <label for='postCode'>Post Code</label>
                    <input type='text' name='postCode' placeholder={form.senderAddress.postCode} />
                </div>
            </div>
            <div className='Country-Container form-container'>
                <label for='country'>Country</label>
                <input type='text' name='country' placeholder={form.senderAddress.country} />
            </div>
        </div>
    );
};


const EditFormFooter = ({form}) => {
    return (
        <div className='Edit-Form-Footer'></div>
    )
};


export default EditFormContainer;
