import './InvoiceDetails.css';
import { useLocation } from "react-router-dom";

import dataJSON from '../../TEMP_DATA/data.json';
import backArrow from '../../images/assets/icon-arrow-left.svg';
import { useEffect, useState } from 'react';

const InvoiceDetails = () => {
    const location = useLocation();
    const invoiceId = location.pathname.split('/invoice/')[1];

    const formData = _getDataFromID(invoiceId);

    return (
        <div className='InvoiceDetail-Wrapper'>
            {formData ? 
                <div className='return-page-wrapper'>
                <img src={backArrow} alt=''/>
                Go back
                <div className='Back-Invoice-Item-Status-Wrapper'>
                    <span>Status</span>
                    <div className=''>

                    </div>
                </div>
                </div>
                :
                <span>Loading...</span>
            }

        </div>
    )
};


function _getDataFromID(id) {
    const foundItem = dataJSON.find((item) => item.id === id);
    return foundItem;
}

export default InvoiceDetails;
